function drawLineChart(target_id, values, option_values){
   var data = google.visualization.arrayToDataTable(values);

   var options = {
     pointSize: 3, // 점크기
     width: 500, // 넓이
     height: 200, // 높이
     //legend: { position: 'top' ,alignment :'end'},
     legend : {position : 'none'},
     vAxis: {
       viewWindowMode:'explicit',
       viewWindow: {
         max:10.3,
         min:0
       },
       gridlines : {color: '#D5D5D5', count: 5}
     }
   };

 options = $.extend(options, option_values);


 var chart = new google.visualization.LineChart(document.getElementById(target_id));
 chart.draw(data, options);
}


function drawCircleChart(target_id, option_values){
 google.charts.load('current', {'packages' : ['corechart']});

 //Create the data table.
 //데이터 테이블 생성
 var data = new google.visualization.DataTable();
 data.addColumn('string', 'Topping');
 data.addColumn('number', 'Slices');

//데이터
var rows = [];
for (element in option_values.rowval) {
 rows.push([element + " (" + option_values.rowval[element]['value'] + ")", option_values.rowval[element]['value']])
}

 data.addRows(rows);

// Set chart options
 // 차트 설정 옵션
 var options = {'width' :'100%', 'height' : '100%',  legend: 'none',
     chartArea :{width:'100%',height:'100%'},
       pieHole: 0.8, // 파이그래프 가운데 홀 크기
       pieSliceText: 'none', // 슬라이스 텍스트 표시여부
       pieStartAngle: 0, // 시작각도
       // pieSliceBorderColor : 'black', // border 컬러
      enableInteractivity : false,
       pieSliceTextStyle: { // 슬라이스 텍스트 색
         color: 'black',
       },
       slices: {
       },
 }

//색입히기
for (element in option_values.rowval) {
 options.slices[element] =  { color: option_values.rowval[element]['color']};
}


 options = $.extend(options, option_values);


 //막대차트 변경시 범례가 잘리기 때문에 가로 세로 크기를 수정한다.
 //var options = {'title' : 'How Much Pizza I Ate Last Night', 'width' :500, 'height' : 400};

 //Instantiate and draw our chart, passing in some options.
 //옵션을 몇가지로 돌리면서 차트를 즉시 작성합니다.
 var chart = new google.visualization.PieChart(document.getElementById(target_id));

  //막대차트로 변경시 아래 주석을 풀면 된다.(변경된 점 PieChart -> BarChart)
  //var chart = new google.visualization.BarChart(document.getElementById('chart_div'));
 chart.draw(data, options);

 $('#'+target_id).append('<div class="text" style="'+option_values.msg_style+'">'+option_values.msg+'</div>');

return rows;
}



function drawBarChart(target_id, values, colors , option_values) {
 var chartDiv = document.getElementById(target_id);
     var data = google.visualization.arrayToDataTable(values);
 if(typeof(colors)=='undefind'){
   colors = {};
 }
     var options = {
   //  title : '수원 삼성 블루윙즈', 타이틀
       width: 300, // 넓이
       height: 200, // 높이
       bar : {groupWidth : '80%'}, //bar 사이즈
       colors: $.extend(['blue', '#ff0000'], colors),
       //legend : {position : 'top' ,alignment :'end'},
       legend : {position : 'none'}
     };

     options = $.extend(options, option_values);

      var classicChart = new google.visualization.ColumnChart(chartDiv);
       classicChart.draw(data, options);
   }

